import styled from 'styled-components';

export const Wrapper = styled.div`
   display: flex;
   align-items: center;
   min-height: 100px;
   background: var(--darkGrey);
   padding: 0 20px
`;

export const Content = styled.div`
   display: flex;
   max-width: var(--maxWidth);
   width: 100%
`;
